import React from 'react'

export default function ImageUpload(props) {
  const handleImageAdded = (file) => {
    console.log(file);
  };
  return (
    <>
      <div className='border-primary d-flex align-items-center justify-content-center position-relative' style={{height:"157px"}}>
        <label className='text-center primary-color'>
            Drag & Drop Here<br/>
            Or<br/>
            Browse<br/>
        </label>

        <input 
          className='position-absolute w-100 h-100' 
          type="file"
          id="file_upload"
          accept=".png, .jpg, .jpeg"
          multiple
          style={{opacity:"0"}}
          onChange={e => handleImageAdded(e)}
        />
    </div>
    </>


  );
}
