import React from 'react'

export default function ImageUpload(props) {
  const handleImageAdded = async (e) => {
    let filesArray = [];
    if( e.target?.files){
      filesArray.push(e.target?.files)
    }
    console.log(filesArray);
    // Reset file input value to clear the selected files
    e.target.value = null;
  };
  return (
    <>
      <div className='border-primary d-flex align-items-center justify-content-center position-relative cursor-pointer' style={{height:"157px"}}>
        <label className='text-center primary-color'>
            Drag & Drop Here<br/>
            Or<br/>
            Browse<br/>
        </label>
        <input 
          className='position-absolute w-100 h-100 cursor-pointer' 
          type="file"
          id="file_upload"
          accept=".png, .jpg, .jpeg"
          multiple
          style={{opacity:"0"}}
          onChange={handleImageAdded}
        />
    </div>
    </>


  );
}
