import React from 'react'

export default function ImageUpload(props) {
  const handleImageAdded = (file) => {
    props.onUploadImage(file)
  };
  return (
    <>
      <div className='border dotted d-flex align-items-center justify-content-center' style={{height:"157px"}}>
        <label className='text-center'>
            Drag & Drop Here<br/>
            Or<br/>
            Browse<br/>
        </label>
      </div>
      <input 
        className='' 
        type="file"
        id="file_upload"
        accept=".png, .jpg, .jpeg"
        multiple
        onChange={e => handleImageAdded(e)}
      />

    </>


  );
}
