import React from 'react'
export default function ImageUpload(props) {
  const handleImageAdded = async (e) => {
    const uploadFileList = [...e.target?.files];
    console.log(uploadFileList);
    abc(uploadFileList)

  };
  const abc = async (imgs, sizes = true) => {
    const formData = new FormData();
    imgs.forEach((imgBlob, index) => {
      // Append each file with a unique key, for example, "my_file_0", "my_file_1", etc.
      formData.append(`my_file_${index}`, imgBlob);
    });
    console.log(formData);
  }

  return (
    <>
      <div className='border-primary d-flex align-items-center justify-content-center position-relative' style={{height:"157px"}}>
        <label className='text-center primary-color'>
            Drag & Drop Here<br/>
            Or<br/>
            Browse<br/>
        </label>
        <input 
          className='position-absolute w-100 h-100' 
          type="file"
          id="file_upload"
          accept=".png, .jpg, .jpeg"
          multiple
          style={{opacity:"0"}}
          onChange={handleImageAdded}
        />
    </div>
    </>


  );
}
