import React,{useState,useEffect} from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios"
export default function Login() {
    const navigate = useNavigate();
    let [isLogin, setLogin] = useState(true)
    const [getRegisterData, setRegisterData] = useState({})
    const handleChange = (event) =>{
        setRegisterData((prev)=>({...prev,[event.target.name]:event.target.value}))
    }
    const showRegisterForm = (e) =>{
        isLogin = !isLogin;
        setLogin(isLogin)
    }

    const login = async () =>{
        try {
            const { data } = await axios.post('http://localhost:7000/user-ms/login',getRegisterData,{
                headers: {
                  'Content-Type': 'application/json'
                }
            });
            navigate("/")
            localStorage.setItem("x-access-token",data.token);
            localStorage.setItem('login', true)
        } catch (error) {
            localStorage.setItem('login', false)
        }

    }
    const registerUser = async () =>{
        try {
            const { data } = await axios.post('http://localhost:7000/user-ms/register',getRegisterData,{
                headers: {
                  'Content-Type': 'application/json'
                }
            });
            setLogin(true)
        } catch (error) {

        }
    }
    //If there is already a user login, this hook navigates you to the home page
    useEffect(()=>{
        let login = localStorage.getItem('login');
        if(!login){
            navigate("/")
        }
    })
    return (
        <main className="form-signin" style={{padding:"10% 0",margin:"0 auto",maxWidth:"400px"}}>

                <div className="form-floating mb-3 text-center font-bold">
                    E - Auction
                </div>
                {
                    !isLogin &&  <>
                    <div className="form-floating mb-3">
                        <input type="text" className="form-control" id="floatingFirstname" placeholder="First Name" name="firstName" onChange={handleChange}/>
                         <label htmlFor="floatingFirstname">First Name</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="email" className="form-control" id="floatingLastName" placeholder="Last Name" name="lastName" onChange={handleChange}/>
                        <label htmlFor="floatingLastName">Last Name</label>
                    </div>
                    </>
                }

                <div className="form-floating mb-3">
                    <input type="email" className="form-control" id="floatingInput" placeholder="name@example.com" name="email" onChange={handleChange}/>
                    <label htmlFor="floatingInput">Email address</label>
                </div>
                <div className="form-floating  mb-3">
                    <input type="password" className="form-control" id="floatingPassword" placeholder="Password" name="password" onChange={handleChange}/>
                <label htmlFor="floatingPassword">Password</label>
                </div>

                <button className="w-100 btn btn-lg btn-dark" type="submit" onClick={()=>{
                    isLogin ? login() : registerUser()
                }}>{!isLogin ? 'Sign in': 'Sign Up'}</button>
                <p className='mt-3'>Already User? <span className='text-primary' style={{cursor:'pointer'}} onClick={showRegisterForm}> Create a new account</span></p>
         </main>
    )
}
