import React, { useState } from 'react';
import ImageUpload from '../components/ImageUpload';
import AuthUser from '../components/auth/AuthUser';
import { baseURL } from '../constants/alpha-env.constant';

export default function ProductForm() {
  const { http } = AuthUser();
  const [images, setImages] = useState([]);
  const [getRegisterData, setRegisterData] = useState({ title: '', slug: '' });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setRegisterData((prev) => ({ ...prev, [name]: value, slug: generateSlug(value) }));
  };

  const generateSlug = (title) => {
    return title.toLowerCase().replace(/\s+/g, '-');
  };

  const registerUser = async () => {
    try {
      const imagesToSend = images
        .filter(res => res.data.filename)
        .map(res => res.data.filename);

      const requestData = { ...getRegisterData, images: imagesToSend };
      const { data } = await http.post(`${baseURL}/product-ms/add`, requestData);
      console.log(data);
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  return (
    <main className="form-signin">
      <div className='container'>
        <div className="form-floating mb-3 text-center fw-bold fs-1">Add Product</div>
        <div className="mb-3">
          <label className="form-label">Title</label>
          <input type="text" className="form-control" name="title" onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label" style={{marginRight:"20px"}}>Slug:</label>
          <span className='primary-color'>{getRegisterData.slug}</span>

        </div>
        {/* Other form fields */}
        <div className="mb-3">
          <label htmlFor="formFileSm" className="form-label">Images</label>
          <div className='col-md-12'>
            <ImageUpload multipleImage={true} imageRender={images} setImageRender={setImages} />
          </div>
          {/* Render uploaded images */}
          <div className='col-md-12'>
            <ul className='d-flex p-0' style={{ listStyleType: "none" }}>
              {
                images.length > 0 && (
                  images.map((image, index) => (
                    <li key={index} className='ml-2'>
                      <img src={`${baseURL}/images/${image.data.filename}`} alt={`Image ${index}`} style={{ width: '150px', height: '150px', objectFit: 'cover' }} className='img-thumbnail' />
                    </li>
                  ))
                )
              }
            </ul>
          </div>
          <div>
            <button className="w-100 btn btn-lg btn-dark" type="submit" onClick={registerUser}>
              Submit
            </button>
          </div>
        </div>
      </div>
    </main>
  );
}
