import React from 'react'

export default function ImageUpload(props) {
  const handleImageAdded = (file) => {
    props.onUploadImage(file)
  };
  return (
    <>
      <div className='border dotted flex align-items-center' style={{height:"157px"}}>
        <label>
            <div>Drag & Drop Here</div>
            <div>Or</div>
            <div>Browse</div>
            <div></div>
        </label>
      </div>
      <input 
        className='' 
        type="file"
        id="file_upload"
        accept=".png, .jpg, .jpeg"
        multiple
        onChange={e => handleImageAdded(e)}
      />

    </>


  );
}
