import React, { useState } from 'react'
import ImageUpload from '../components/ImageUpload'
import AuthUser from '../components/auth/AuthUser';
import { baseURL } from '../constants/alpha-env.constant'
export default function ProductForm() {
    const {http} = AuthUser();
    const [images,setImages] = useState([]);
    const [getRegisterData, setRegisterData] = useState({});
    const handleChange = (event) => {
        setRegisterData((prev) => ({ ...prev, [event.target.name]: event.target.value }));
    };
    const registerUser = async () => {
        try {
          const imagesToSend = images
            .filter(res => res.data.filename)
            .map(res => res.data.filename); // Assuming you want to send only the filenames

          const requestData = { ...getRegisterData, images: imagesToSend };
          const { data } = await http.post(`${baseURL}/product-ms/add`, getRegisterData);
          console.log(data);
        } catch (error) {
          console.error('Error registering user:', error);
        }
      };
  return (
    <main className="form-signin ">
        <div className='container'>
            <div className="form-floating mb-3 text-center fw-bold fs-1">Add Product</div>
            <div class="mb-3">
                <label  class="form-label">Title</label>
                <input type="text" class="form-control" name="title" onChange={handleChange}/>
            </div>
            <div class="mb-3">
                <label  class="form-label">Slug</label>
            </div>
            <div  class="mb-3">
                <label  class="form-label">Category</label>
                <select class="form-select" aria-label="Category" name="category" onChange={handleChange}>
                    <option selected></option>
                    <option value="car">Car</option>
                    <option value="House">House</option>
                    <option value="jwelry">Jwelry</option>
                    <option value="watches">Watches</option>
                </select>
            </div>
            <div class="mb-3">
                <label  class="form-label">Description</label>
                <textarea class="form-control" rows="3"  name="description" onChange={handleChange}></textarea>
            </div>
            <div className='row'>
                <div className='col-md-6'>
                    <div class="mb-3">
                        <label  class="form-label">Starting Bid</label>
                        <input type="number" class="form-control"  name="startingBid" onChange={handleChange}/>
                    </div>
                </div>
                <div className='col-md-6'>
                    <div class="mb-3">
                        <label  class="form-label">Auction Duration</label>
                        <input type="number" class="form-control"  name="auctionDuration" onChange={handleChange}/>
                    </div>
                </div>
            </div>
            <div class="mb-3">
                <label for="formFileSm" class="form-label">Images</label>
                <div className='col-md-12'>
                    <ImageUpload multipleImage={true} imageRender={images} setImageRender={setImages}/>
                </div>

                <div className='col-md-12'>
                    <ul className='d-flex p-0' style={{listStyleType:"none"}}>
                        {
                            images.length > 0 && (
                                images.map((image, index) => (
                                    <li key={index} className='ml-2'>
                                        {/* Constructing the src attribute using template literals */}
                                        <img src={`${baseURL}/images/${image.data.filename}`} alt={`Image ${index}`}  style={{ width: '150px', height: '150px', objectFit: 'cover' }} className='img-thumbnail'/>
                                    </li>
                                ))
                            )
                        }
                    </ul>


                </div>
                <div>
                    <button className="w-100 btn btn-lg btn-dark" type="submit" onClick={() =>  registerUser()}>
                        Submit
                    </button>
                </div>
            </div>
        </div>   
    </main>
  )
}
